import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Policy = props => (
    <tr>
        <td className="align-middle">{props.policy.maxPercentageIncrease}</td>
        <td className="align-middle">{props.policy.year}</td>
        {/* <td>
            <Link to={"/edit/"+props.agreement._id}>Edit</Link> | <a href="#" onClick={() => { props.payRent(props.agreement._id) }}>Pay rent</a>
        </td> */}
        <td className="align-middle">
            {/* <Link to={"/edit/"+props.agreement._id} className="btn btn-primary">Edit</Link> */}
            <button className="btn btn-danger ml-2" onClick={() => { props.payRent(props.agreement._id) }}>Pay Rent</button>
        </td>
        <td>
            <img src={props.agreement.image} alt="Agreement" className="rounded" style={{ width: '200px', height: '100px' }} />
        </td>
    </tr>
)

export default class PolicyDisplay extends Component {

    constructor(props) {
        super(props);

        this.state = {policies: []};
    }

    componentDidMount() {
        axios.get('http://localhost:5000/policy')
        .then(response => {
            this.setState({ policies: response.data })
        })
        .catch((error) => {
            console.log(error);
        })
    }



    policyList() {
        return this.state.policies.map(currentpolicy => {
            return <Policy policy={currentpolicy} key={currentpolicy._id}/>;
        })
    }

    render() {
        return (
            <div>
                <img src="https://images.unsplash.com/photo-1641757728612-8d777dd1a251?q=80&w=2230&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt="Policy Image" style={{ width: '100%', height: 'auto' }} />
                <h3>Rent Stabilization Policy</h3>
                <table className="table">
                    <thead className="thead-light">
                        <tr>
                            <th>Year</th>
                            <th>Maximum Rent Hike Percentage</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.policyList() }
                    </tbody>
                </table>
            </div>
        );
    }
}
