import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Agreement = props => (
    <tr>
        <td className="align-middle">{props.agreement.landlord}</td>
        <td className="align-middle">${props.agreement.rent}</td>
        <td className="align-middle">{props.agreement.address}</td>
        <td className="align-middle">${props.agreement.rentHiked}</td>
        {/* <td>
            <Link to={"/edit/"+props.agreement._id}>Edit</Link> | <a href="#" onClick={() => { props.payRent(props.agreement._id) }}>Pay rent</a>
        </td> */}
        <td className="align-middle">
            {/* <Link to={"/edit/"+props.agreement._id} className="btn btn-primary">Edit</Link> */}
            <button className="btn btn-danger ml-2" onClick={() => { props.updateRent(props.agreement._id) }}>Update Rent</button>
        </td>
        <td>
            <img src={props.agreement.image} alt="Agreement" className="rounded" style={{ width: '200px', height: '100px' }} />
        </td>
    </tr>
)

export default class EditAgreement extends Component {
    constructor(props) {
        super(props);
        this.updateRent =  this.updateRent.bind(this);
        this.state = {agreements: [], maxPercentageIncrease: 0, year: 0};
    }

    componentDidMount() {
        axios.get('http://localhost:5000/agreement')
        .then(response => {
            this.setState({ agreements: response.data })
        })
        .catch((error) => {
            console.log(error);
        })
        axios.get('http://localhost:5000/policy')
        .then(response => {
            this.setState({ year: response.data[0].year })
            this.setState({ maxPercentageIncrease: response.data[0].maxPercentageIncrease })
        })
        .catch((error) => {
            console.log(error);
        })
    }

    updateRent(id) {
        axios.post('http://localhost:5000/agreement/update/' + id)
        .then(res => console.log(res.data));
        axios.get('http://localhost:5000/agreement')
        .then(response => {
            this.setState({ exercises: response.data })
        })
        .catch((error) => {
            console.log(error);
        });
    }

    agreementList() {
        return this.state.agreements.map(currentagreement => {
            return <Agreement agreement={currentagreement}  payRent={this.payRent} key={currentagreement._id}/>;
        })
    }

    render() {
        return (
            <div>
                <div className="text-center">
                    <img src="https://i.imgur.com/FsRZ6oq.png" alt="Policy Image" className="rounded" style={{ width: '30%', height: 'auto' }} />    
                </div>
                <h3>Maximum rent hike in {this.state.year}: {this.state.maxPercentageIncrease}%</h3>
                <table className="table table-striped">
                    <thead className="thead-light">
                        <tr>
                            <th >Landlord</th>
                            <th >Rent</th>
                            <th >Address</th>
                            <th >Rent Hiked in 2024</th>
                            <th >Maximum rent hike percentage</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.agreementList() }
                    </tbody>
                </table>
            </div>
        );
    }
}
